                "reverse": {
                    "type": "Boolean",
                    "desc": "Apply the model size to the second panel (by default it applies to the first)",
                    "category": "model"
                },
                "unit": {
                    "type": "String",
                    "desc": "CSS unit for the model",
                    "default": "%",
                    "values": [
                        "%",
                        "px"
                    ],
                    "category": "model",
                    "required": false
                },
